import React from "react";
import { Button } from "react-bootstrap";

const style = { textAlign: "center" };
const styleNoJob = {
  textAlign: "center",
  marginTop: "25%"
};
const descriptionStyle = { textAlign: "left", whiteSpace: "pre-wrap" };

const JobInformation = props => {
  if (props.loading === true) {
    return (
      <div style={styleNoJob}>
        {"Searching for Jobs. It shouldn't take long"}
      </div>
    );
  }
  if (props.job === null) {
    return <div style={styleNoJob}>{"Please Click on a Job"}</div>;
  } else {
    return (
      <div style={style}>
        <br />
        <br />
        {props.job.jobTitle} <br />
        {props.job.company} <br />
        {props.job.jobType} <br />
        {"% Resume Match: " + Math.random() * 101}
        <br />
        <br />
        <Button variant="primary" href={props.job.link}>
          {"Apply through " + props.job.source}
        </Button>
        <br />
        <br />
        <div style={descriptionStyle}>{props.job.jobDescription}</div> <br />
      </div>
    );
  }
};

export default JobInformation;
