import React, { Component } from "react";
import Navigation from "./components/Navigation";
import HomePage from "./views/HomePage";
import { getRequest } from "./requests/axiosLocalHost";

const axios = require("axios");
class App extends Component {
  constructor() {
    super();
    this.state = { jobs: null, loading: false };

    this.passFormData = this.passFormData.bind(this);
    this.homeReset = this.homeReset.bind(this);
    this.setLoading = this.setLoading.bind(this);
  }

  homeReset() {
    this.setState({ jobs: null, loading: false });
  }

  passFormData(title, location, type) {
    this.setState({ loading: true }, async (title, loaction, type) => {
      this.setState({
        jobs: await getRequest(title, location, type),
        loading: false
      });
    });
    // this.setState({
    //   jobs: getRequest(title, location, type),
    //   loading: false
    // });
  }

  setLoading() {
    this.setState({ loading: true });
  }

  render() {
    // console.log(typeof this.state.jobs);
    console.log(this.state.loading);
    let pageRender = () => {
      if (this.state.jobs === null && this.state.loading === false) {
        return (
          <HomePage
            passFormData={this.passFormData}
            setLoading={this.setLoading}
          />
        );
      } else if (this.state.jobs === undefined && this.state.loading === true) {
        return <div>{"Jobs Loading"}</div>;
      } else {
        // if (typeof list === Object)
        //   return <div>{"Jobs Could Not Be Found"}</div>;
        return <div>{"Jobs Ready"}</div>;
      }
    };

    return (
      <div className="App">
        <Navigation homeReset={this.homeReset} />
        {pageRender()}
      </div>
    );
  }
}

export default App;
