import React from "react";
import { Container, Row, Col, Button } from "react-bootstrap";
import "../css/components/JobInformation.css";

const descriptionStyle = { textAlign: "left", whiteSpace: "pre-wrap" };

const JobInformation = props => {
  if (props.job === null) {
    return <div className="jobInformation">{"Please Click on a Job"}</div>;
  } else {
    return (
      <div className="jobInformation">
        <Row className="jobInformationRow">
          <Col lg={12}>{props.job.jobTitle}</Col>
        </Row>
        <Row className="jobInformationRow">
          <Col lg={12}>{props.job.company}</Col>
        </Row>
        <Row className="jobInformationRow">
          <Col lg={12}>{props.job.jobType}</Col>
        </Row>
        <Row className="jobInformationRow">
          <Col lg={12}>{"% Resume Match: " + Math.random() * 101}</Col>
        </Row>
        <Row className="jobInformationRow">
          <Col lg={12}>
            <Button variant="primary" href={props.job.link}>
              {"Apply through " + props.job.source}
            </Button>
          </Col>
        </Row>
        <Row className="jobInformationRow">
          <Col className="description" lg={12}>
            {props.job.jobDescription}
          </Col>
        </Row>
      </div>
    );
  }
};

export default JobInformation;
