import React from "react";
import { Button } from "react-bootstrap";

const style = { textAlign: "center" };
const descriptionStyle = { textAlign: "left", whiteSpace: "pre-wrap" };

const JobInformation = props => {
  if (props.job === null) {
    return <div style={style}>{"Please Click on a Job"}</div>;
  } else {
    return (
      <div style={style}>
        {props.job.jobTitle} <br />
        {props.job.company} <br />
        {props.job.jobType} <br />
        {"% Resume Match: " + Math.random() * 101} <br />
        <Button variant="primary" href={props.job.link}>
          {"Apply through " + props.job.source}
        </Button>{" "}
        <br />
        <br />
        <span style={descriptionStyle}>{props.job.jobDescription}</span> <br />
      </div>
    );
  }
};

export default JobInformation;
